// Import PixiJS
import * as PIXI from 'pixi.js';

// Khai báo kích thước của canvas
const width: number = 800;
const height: number = 600;

// Tạo một PIXI Application
const app: PIXI.Application = new PIXI.Application({
    width: width,
    height: height,
    backgroundColor: 0x1099bb,
    resolution: window.devicePixelRatio || 1,
});

// Thêm canvas vào DOM
document.body.appendChild(app.view);

// Tạo người chơi (thùng)
const player: PIXI.Sprite = PIXI.Sprite.from('player.png');
player.anchor.set(0.5);
player.x = width / 2;
player.y = height - 100;
app.stage.addChild(player);

// Tạo mảng để lưu trữ quả trái và quả bom
const fruits: PIXI.Sprite[] = [];
const bombs: PIXI.Sprite[] = [];

// Tạo timer để tạo quả trái và quả bom mỗi giây
const timer: NodeJS.Timeout = setInterval(() => {
    createFruit();
    createBomb();
}, 1000);

// Tạo hàm để tạo quả trái
function createFruit(): void {
    const fruit: PIXI.Sprite = PIXI.Sprite.from('fruit.png');
    fruit.anchor.set(0.5);
    fruit.x = Math.random() * width;
    fruit.y = -50;
    fruits.push(fruit);
    app.stage.addChild(fruit);
}

// Tạo hàm để tạo quả bom
function createBomb(): void {
    const bomb: PIXI.Sprite = PIXI.Sprite.from('bomb.png');
    bomb.anchor.set(0.5);
    bomb.x = Math.random() * width;
    bomb.y = -50;
    bombs.push(bomb);
    app.stage.addChild(bomb);
}

// Điều khiển người chơi
window.addEventListener('keydown', (e: KeyboardEvent) => {
    const speed: number = 5;
    if (e.key === 'ArrowLeft') {
        player.x -= speed;
    } else if (e.key === 'ArrowRight') {
        player.x += speed;
    }
});

// Hàm cập nhật trạng thái của trò chơi
function update(): void {
    fruits.forEach((fruit: PIXI.Sprite, index: number) => {
        fruit.y += 2;
        // Kiểm tra va chạm với người chơi
        if (fruit.y > height - 100 && Math.abs(fruit.x - player.x) < 30) {
            app.stage.removeChild(fruit);
            fruits.splice(index, 1);
        }
    });

    bombs.forEach((bomb: PIXI.Sprite, index: number) => {
        bomb.y += 2;
        // Kiểm tra va chạm với người chơi
        if (bomb.y > height - 100 && Math.abs(bomb.x - player.x) < 30) {
            app.stage.removeChild(bomb);
            bombs.splice(index, 1);
        }
    });
}

// Lặp lại cập nhật trạng thái của trò chơi
app.ticker.add(() => {
    update();
});
